int sum1(int x){x %= P; return x * (x + 1) % P * IV2 % P - 1;}
int sum2(int x){x %= P; return x * (x + 1) % P * (2 * x + 1) % P * IV6 % P - 1;}

int prm[_], cprm; bool noprime[_];
void sieve(){
	for(int i = 2 ; i < _ ; ++i){
		if(!noprime[i]) prm[++cprm] = i;
		for(int j = 1 ; j <= cprm && prm[j] * i < _ ; ++j){
			noprime[i * prm[j]] = 1; if(i % prm[j] == 0) break;
		}
	}
}

#define pii pair < int , int >
pii solve(int l, int r){
	static bool arr[BLOCK / 2 + 5]; memset(arr, 0, sizeof(arr));
	int sum1 = 0, sum2 = 0;
	for(int i = 1; i <= cprm && prm[i] * prm[i] <= r ; ++i)
		for(int j = max(prm[i], (l + prm[i] - 1) / prm[i]) * prm[i] ; j <= r ; j += prm[i]) arr[j - l] = 1;
	for(int i = 0 ; i <= r - l ; ++i)
		if(!arr[i]){int val = (l + i) % P; sum1 += val; sum2 = (sum2 + val * val) % P;}
	return pii(sum1 % P, sum2);
}
pii getval(int n){
	if(n % BLOCK == 0) return pii(pw1[n / BLOCK], pw2[n / BLOCK]);
	pii ans;
	if(n % BLOCK <= BLOCK / 2){
		pii partial = solve(n / BLOCK * BLOCK + 1, n);
		ans = pii(partial.first + pw1[n / BLOCK], partial.second + pw2[n / BLOCK]);
	}else{
		pii partial = solve(n, n / BLOCK * BLOCK + BLOCK);
		ans = pii(pw1[n / BLOCK + 1] - partial.first, pw2[n / BLOCK + 1] - partial.second);
	}
	return pii((ans.first + P) % P, (ans.second + P) % P);
}

signed main(){
	sieve();
	int n; cin >> n; assert(n >= 4 && n <= 1e11);
	pii pw1 = getval(n), pw2 = getval(n / 2);
	int s1 = (sum1(n) - pw1.first + pw2.first + P) % P,
		s2 = (sum2(n) - pw1.second + pw2.second + P) % P;
	cout << (s1 * s1 % P - s2 + P) * IV2 % P << '\n';
	return 0;
}